def genpatt(values)
  patt = []
  start = nil
  last = nil
  values.each do |v|
    if start.nil?
      start = v
      last = v
    elsif v == last + 1
      last = v
    else
      if last == start
        patt << "#{start}"
      else
        patt << "#{start}..#{last}"
      end
      start = v
      last = v
    end 
  end
  if last == start
    patt << "#{start}"
  else
    patt << "#{start}..#{last}"
  end
  patt.join ','
end

def sel_algo(algos, opt_size, with_a)
  res = {}
  algos.each_key do |k|
    res[[k, true]] = []
    res[[k, false]] = []
  end
  (1..255).each do |i|
    min_size = nil
    min_time = nil
    sel_name = nil
    sel_use_a = false
    algos.each do |aname, data|
      size = data[:size][i]
      time = data[:time][i]
      # puts "#{aname}, #{i} size: #{size} time: #{time}"
      use_a = false
      if with_a && data.has_key?(:size_a) && data.has_key?(:time_a)
        size_a = data[:size_a][i]
        time_a = data[:time_a][i]
        # puts "#{aname}, #{i} size_a: #{size_a} time_a: #{time_a}"
        use_a = if opt_size
          (size_a < size or (size_a == size && time_a <= time))
        else
          (time_a < time or (time_a == time && size_a <= size))
        end
        if use_a
          size = size_a
          time = time_a
        end
      end
      if sel_name.nil? or
          (opt_size and (size < min_size or (size == min_size && time < min_time))) or
          (!opt_size and (time < min_time or (time == min_time && size < min_size)))
        min_size = size
        min_time = time
        sel_name = aname
        sel_use_a = use_a
      end
    end
    # puts "#{i} #{min_size} #{sel_name} #{sel_use_a}"
    res[[sel_name, sel_use_a]].push i
  end
  res
end

