def gen_code(res, level)
  puts "#{INDENT*level}case m"
  res = res.sort {|a, b| a[1].length <=> b[1].length}
  last_key, _ = res.pop
  res.each do |(key, indexes)|
    unless indexes.empty?
      puts "#{INDENT*level}when #{genpatt(indexes)}"
      puts "#{INDENT*(level+1)}#{key}"
    end
  end
  puts "#{INDENT*level}else"
  # puts "#{INDENT*level}when #{genpatt(indexes)}"
  puts "#{INDENT*(level+1)}#{last_key}"
  puts "#{INDENT*level}end"
end

